import React, { Component } from 'react';
import './App.css';
import Nav from './sections/nav';
import Top from './sections/top';
import About from './sections/about';
import Location from './sections/location';
import Contact from './sections/contact';

class App extends Component {

	scroll = (nav) => {
		let section = document.getElementById(nav)
		section.scrollIntoView()
		console.log(nav)
	}

	render() {
		return (
			<div className="App">
				<h1>Waltham Vision Care</h1>
				<nav>
					<button onClick={() => this.scroll('about')}>About Us</button>
					<button onClick={() => this.scroll('location')}>Find Us</button>
					<button onClick={() => this.scroll('contact')}>Contact Us</button>
				</nav>
				<main>
					<Top />
					<About id='about' />
					<Location id='location' />
					<Contact id='contact' />
				</main>
				<footer>&copy; Waltham Vision Care 2018. All rights reserved.</footer>
			</div>
		);
	}
}

export default App;
