import React, { Component } from 'react';
import 'react-sticky-header/styles.css';
import StickyHeader from 'react-sticky-header';

export default class Nav extends Component {
    scroll = (nav) => {
        let section = document.getElementById(nav)
        section.scrollIntoView()
        console.log(nav)
    }

    render() {
        return (
            <StickyHeader
                // This is the sticky part of the header.
                header={
                    <nav>
                        <button onClick={() => this.scroll('about')}>About Us</button>
                        <button onClick={() => this.scroll('location')}>Find Us</button>
                        <button onClick={() => this.scroll('contact')}>Contact Us</button>
                    </nav>
                }>
                <section>
                    <p>
                        This section will be what the sticky header scrolls over before entering into
                        sticky state. See the gif above or run the test story book to see examples.
                </p>
                </section>
                np</StickyHeader>
        )
    }
}
